package com.zhangbin.jpa.controller;import com.zhangbin.jpa.entity.UserEntity;import com.zhangbin.jpa.jpa.UserJpa;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping(value = "/user")public class UserController {        @Autowired    private UserJpa userJpa;        /*     *     * @Author zhangbin     * @Description //TODO      * @Date 11:14 2018-12-28     * @Param []     * @return java.util.List<com.zhangbin.jpa.entity.UserEntity>     **/    @RequestMapping(value = "/list",method = RequestMethod.GET)    public List<UserEntity> list() {        return userJpa.findAll();    }    @RequestMapping(value = "/save",method = RequestMethod.GET)    public UserEntity save(UserEntity entity) {        return userJpa.save(entity);    }    @RequestMapping(value = "/delete",method = RequestMethod.GET)    public List<UserEntity> delete(Long id) {        userJpa.deleteById(id);        return userJpa.findAll();    }}